import React, { useState } from 'react';
import API from '../../api';

const CreateSoftware = () => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [accessLevels, setAccessLevels] = useState<string[]>([]);

  const handleCheckboxChange = (level: string) => {
    setAccessLevels((prev) =>
      prev.includes(level) ? prev.filter((l) => l !== level) : [...prev, level]
    );
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await API.post('/software', { name, description, accessLevels });
      alert('Software created');
    } catch (err) {
      alert('Creation failed');
    }
  };

  return (
    <form className="create-software-form" onSubmit={handleSubmit}>
      <h2>Create Software</h2>
      <input value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" required />
      <textarea value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" required />
      <div className="checkbox-group">
        <label>
          <input type="checkbox" value="Read" onChange={() => handleCheckboxChange('Read')} />
          Read
        </label>
        <label>
          <input type="checkbox" value="Write" onChange={() => handleCheckboxChange('Write')} />
          Write
        </label>
        <label>
          <input type="checkbox" value="Admin" onChange={() => handleCheckboxChange('Admin')} />
          Admin
        </label>
      </div>
      <button type="submit">Create</button>
    </form>
  );
};

export default CreateSoftware;
