import React, { useState } from 'react';
import API from '../../api';
import { useNavigate, Link } from 'react-router-dom';
import Swal from 'sweetalert2';
import '../../styles/Auth.css'; // <-- Add this import

const Signup = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSignup = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await API.post('/auth/signup', { username, password });
      Swal.fire('Success', 'Signup successful! Please login.', 'success');
      setTimeout(() => navigate('/login'), 1500);
    } catch (err: any) {
      if (err.response && err.response.data && err.response.data.message === 'User exists') {
        Swal.fire('Error', 'User already exists!', 'error');
      } else {
        Swal.fire('Error', 'Signup failed', 'error');
      }
    }
  };

  return (
    <form className="auth-container" onSubmit={handleSignup}>
      <h2>Signup</h2>
      <input value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" required />
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required />
      <button type="submit">Register</button>
      <div>
        Already have an account? <Link to="/login">Login</Link>
      </div>
    </form>
  );
};

export default Signup;
